mapper.insert:
insert into web5_board ( boardnum, id, title, contents
, originalfile , savedfile
) values ( web5_board_seq.nextval, #{id}, #{title}, #{contents}
, #{originalfile} , #{savedfiles}
)

mapper.update:
update web5_board
set hits = hits + 1
where boardnum = #{boardnum}

mapper.delete:
delete
from
	web5_board
where
	boardnum = #{boardnum}
	and id = #{id}

mapper.update:
update web5_board
set
	title = #{title}
	, contents = #{contents}

	, originalfile = #{originalfile}
	, savedfile = #{savedfiles}

where
	boardnum = #{boardnum}
	and id = #{id}
